contracts/Educatalyst.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./EduCatalystOrganization.sol";
import "./EducatalystFunding.sol";
import "./EduCatalystMilestone.sol";

/**
 * @title EduCatalystCore
 * @dev Core contract for the EduCatalyst platform that coordinates between other contracts
 */
contract EduCatalystCore {
    address public owner;
    uint256 public platformFeePercent;
    uint256 public constant MAX_FEE = 500; // Maximum 5% fee

    EduCatalystOrganization public organizationContract;
    EduCatalystFunding public fundingContract;
    EduCatalystMilestone public milestoneContract;

    constructor(uint256 _platformFeePercent) {
        require(_platformFeePercent <= MAX_FEE, "Fee too high");
        owner = msg.sender;
        platformFeePercent = _platformFeePercent;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    function setOrganizationContract(address _organizationContract) external onlyOwner {
        organizationContract = EduCatalystOrganization(_organizationContract);
    }

    function setFundingContract(address _fundingContract) external onlyOwner {
        fundingContract = EduCatalystFunding(_fundingContract);
    }

    function setMilestoneContract(address _milestoneContract) external onlyOwner {
        milestoneContract = EduCatalystMilestone(_milestoneContract);
    }

    function updatePlatformFee(uint256 _newFeePercent) external onlyOwner {
        require(_newFeePercent <= MAX_FEE, "Fee too high");
        platformFeePercent = _newFeePercent;
    }

    // Functions to delegate calls to specific contracts
    function getPlatformFee() external view returns (uint256) {
        return platformFeePercent;
    }

    function transferOwnership(address _newOwner) external onlyOwner {
        require(_newOwner != address(0), "New owner cannot be zero address");
        owner = _newOwner;
    }
}

contracts/EducatalystFunding.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./Educatalyst.sol";
import "./EduCatalystOrganization.sol";

/**
 * @title EduCatalystFunding
 * @dev Manages funding requests and contributions
 */
contract EduCatalystFunding {
    EduCatalystCore public core;
    EduCatalystOrganization public organizationContract;

    enum FundingStatus { Active, Funded, PartiallyFunded, Refunded, Canceled }

    struct FundingRequest {
        uint256 id;
        address payable orgAddress;
        string title;
        string description;
        uint256 fundingGoal;
        uint256 minimumThreshold; // Minimum acceptable funding percentage (in basis points)
        uint256 currentAmount;
        uint256 deadline;
        uint256 eventStart;
        uint256 eventEnd;
        FundingStatus status;
        address[] sponsors;
        mapping(address => uint256) contributions;
        bool eventCompleted; // Flag to mark if the event has been completed
        bool exists;
    }

    mapping(uint256 => FundingRequest) public fundingRequests;
    uint256 public requestCount;

    event FundingRequestCreated(uint256 indexed requestId, address indexed orgAddress, uint256 fundingGoal, uint256 deadline);
    event ContributionMade(uint256 indexed requestId, address indexed sponsor, uint256 amount);
    event FundingGoalReached(uint256 indexed requestId);
    event FundingRequestCanceled(uint256 indexed requestId);
    event RefundIssued(uint256 indexed requestId, address indexed sponsor, uint256 amount);
    event PartialFundingAccepted(uint256 indexed requestId, uint256 amount);
    event FundingDecisionRequired(uint256 indexed requestId, uint256 currentAmount, uint256 fundingGoal);
    event FundingDecisionMade(uint256 indexed requestId, bool proceedWithEvent);
    event EventCompleted(uint256 indexed requestId, uint256 timestamp);
    event EventVerified(uint256 indexed requestId, bool success);

    modifier onlyOrganization() {
        require(organizationContract.isOrganization(msg.sender), "Only registered organizations can call this function");
        _;
    }

    modifier validRequest(uint256 requestId) {
        require(fundingRequests[requestId].exists, "Funding request does not exist");
        _;
    }

    modifier onlyRequestOrg(uint256 requestId) {
        require(fundingRequests[requestId].orgAddress == msg.sender, "Only the organization that created this request can call this function");
        _;
    }

    constructor(address _coreAddress, address _organizationAddress) {
        core = EduCatalystCore(_coreAddress);
        organizationContract = EduCatalystOrganization(_organizationAddress);
        requestCount = 0;
    }

    /**
     * @dev Create a new funding request
     * @param _title Title of the funding request
     * @param _description Description of the event/program
     * @param _fundingGoal Total amount needed
     * @param _minimumThreshold Minimum acceptable percentage (in basis points)
     * @param _deadline Deadline for funding
     * @param _eventStart Start date of the event
     * @param _eventEnd End date of the event
     */
    function createFundingRequest(
        string memory _title,
        string memory _description,
        uint256 _fundingGoal,
        uint256 _minimumThreshold,
        uint256 _deadline,
        uint256 _eventStart,
        uint256 _eventEnd
    ) external onlyOrganization {
        require(_minimumThreshold <= 10000, "Threshold cannot exceed 100%");
        require(_deadline > block.timestamp, "Deadline must be in the future");
        require(_eventStart >= _deadline, "Event must start after funding deadline");
        require(_eventEnd > _eventStart, "Event end must be after start");

        requestCount++;
        uint256 requestId = requestCount;

        FundingRequest storage newRequest = fundingRequests[requestId];
        newRequest.id = requestId;
        newRequest.orgAddress = payable(msg.sender);
        newRequest.title = _title;
        newRequest.description = _description;
        newRequest.fundingGoal = _fundingGoal;
        newRequest.minimumThreshold = _minimumThreshold;
        newRequest.currentAmount = 0;
        newRequest.deadline = _deadline;
        newRequest.eventStart = _eventStart;
        newRequest.eventEnd = _eventEnd;
        newRequest.status = FundingStatus.Active;
        newRequest.exists = true;
        newRequest.eventCompleted = false;

        emit FundingRequestCreated(requestId, msg.sender, _fundingGoal, _deadline);
    }

    /**
     * @dev Get all funding request IDs created by the calling organization
     * @return Array of request IDs
     */
    function getMyRequestIds() external view returns (uint256[] memory) {
        uint256[] memory myRequests = new uint256[](requestCount);
        uint256 resultCount = 0;

        for (uint256 i = 1; i <= requestCount; i++) {
            if (fundingRequests[i].exists && fundingRequests[i].orgAddress == msg.sender) {
                myRequests[resultCount] = i;
                resultCount++;
            }
        }

        // Create correctly sized array with just the organization's requests
        uint256[] memory result = new uint256[](resultCount);
        for (uint256 i = 0; i < resultCount; i++) {
            result[i] = myRequests[i];
        }

        return result;
    }

    /**
     * @dev Contribute funds to a funding request
     * @param _requestId ID of the funding request
     */
    function contribute(uint256 _requestId) external payable validRequest(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        require(request.status == FundingStatus.Active, "Funding is not active");
        require(block.timestamp < request.deadline, "Funding deadline has passed");
        require(msg.value > 0, "Contribution must be greater than 0");

        // Add sponsor to list if first contribution
        if (request.contributions[msg.sender] == 0) {
            request.sponsors.push(msg.sender);
        }

        // Update contribution
        request.contributions[msg.sender] += msg.value;
        request.currentAmount += msg.value;

        emit ContributionMade(_requestId, msg.sender, msg.value);

        // Check if funding goal reached
        if (request.currentAmount >= request.fundingGoal) {
            request.status = FundingStatus.Funded;
            emit FundingGoalReached(_requestId);
        }
    }

    /**
     * @dev Cancel a funding request (only the organization that created it)
     * @param _requestId ID of the funding request
     */
    function cancelFundingRequest(uint256 _requestId) external validRequest(_requestId) onlyRequestOrg(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        require(request.status == FundingStatus.Active, "Cannot cancel a request that isn't active");
        request.status = FundingStatus.Canceled;

        // Refund all sponsors
        for (uint i = 0; i < request.sponsors.length; i++) {
            address sponsor = request.sponsors[i];
            uint256 amount = request.contributions[sponsor];
            if (amount > 0) {
                request.contributions[sponsor] = 0;
                (bool sent, ) = payable(sponsor).call{value: amount}("");
                require(sent, "Failed to send refund");
                emit RefundIssued(_requestId, sponsor, amount);
            }
        }

        emit FundingRequestCanceled(_requestId);
    }

    /**
     * @dev Check if funding decision is required (event date approaching but funding incomplete)
     * @param _requestId ID of the funding request
     */
    function checkFundingDecisionRequired(uint256 _requestId) public validRequest(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        // Only check active requests approaching their event start date
        if (request.status == FundingStatus.Active && 
            block.timestamp >= request.deadline && 
            block.timestamp < request.eventStart &&
            request.currentAmount < request.fundingGoal) {

            emit FundingDecisionRequired(_requestId, request.currentAmount, request.fundingGoal);
        }
    }

    /**
     * @dev Make a decision about proceeding with event despite incomplete funding
     * @param _requestId ID of the funding request
     * @param _proceedWithEvent Whether to proceed with the event using current funds
     */
    function makeFundingDecision(uint256 _requestId, bool _proceedWithEvent) external validRequest(_requestId) onlyRequestOrg(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        // Can only decide on active requests past their deadline but before event starts
        require(request.status == FundingStatus.Active, "Request is not active");
        require(block.timestamp >= request.deadline, "Funding deadline has not passed yet");
        require(block.timestamp < request.eventStart, "Event has already started");
        require(request.currentAmount < request.fundingGoal, "Funding goal already met");

        if (_proceedWithEvent) {
            // Check if minimum threshold is met
            uint256 fundingPercentage = (request.currentAmount * 10000) / request.fundingGoal;
            require(fundingPercentage >= request.minimumThreshold, "Current funding does not meet minimum threshold");

            // Accept partial funding
            request.status = FundingStatus.PartiallyFunded;
            emit PartialFundingAccepted(_requestId, request.currentAmount);
        } else {
            // Cancel event and refund all sponsors
            request.status = FundingStatus.Canceled;

            // Refund all sponsors
            for (uint i = 0; i < request.sponsors.length; i++) {
                address sponsor = request.sponsors[i];
                uint256 amount = request.contributions[sponsor];
                if (amount > 0) {
                    request.contributions[sponsor] = 0;
                    (bool sent, ) = payable(sponsor).call{value: amount}("");
                    require(sent, "Failed to send refund");
                    emit RefundIssued(_requestId, sponsor, amount);
                }
            }

            emit FundingRequestCanceled(_requestId);
        }

        emit FundingDecisionMade(_requestId, _proceedWithEvent);
    }

    /**
     * @dev Process funding requests that are past deadline but have not had decisions made
     * @param _requestId ID of the funding request
     */
    function autoProcessExpiredFunding(uint256 _requestId) external validRequest(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        // Only process active requests past their event start date (meaning no decision was made)
        require(request.status == FundingStatus.Active, "Request is not active");
        require(block.timestamp >= request.eventStart, "Event has not started yet");

        // Automatically cancel and refund
        request.status = FundingStatus.Canceled;

        // Refund all sponsors
        for (uint i = 0; i < request.sponsors.length; i++) {
            address sponsor = request.sponsors[i];
            uint256 amount = request.contributions[sponsor];
            if (amount > 0) {
                request.contributions[sponsor] = 0;
                (bool sent, ) = payable(sponsor).call{value: amount}("");
                require(sent, "Failed to send refund");
                emit RefundIssued(_requestId, sponsor, amount);
            }
        }

        emit FundingRequestCanceled(_requestId);
        emit FundingDecisionMade(_requestId, false);
    }

    /**
     * @dev Mark an event as completed (only the organization that created it)
     * @param _requestId ID of the funding request
     * @param _success Whether the event was successfully completed
     * @param _evidenceUri URI or hash pointing to evidence of completion (could be IPFS)
     */
    function markEventCompleted(uint256 _requestId, bool _success, string memory _evidenceUri) external validRequest(_requestId) onlyRequestOrg(_requestId) {
        FundingRequest storage request = fundingRequests[_requestId];

        require(block.timestamp >= request.eventEnd, "Event end date has not passed yet");
        require(!request.eventCompleted, "Event already marked as completed");
        require(request.status == FundingStatus.Funded || request.status == FundingStatus.PartiallyFunded, 
                "Request must be funded to mark as completed");

        request.eventCompleted = true;

        emit EventCompleted(_requestId, block.timestamp);
        emit EventVerified(_requestId, _success);
    }

    /**
     * @dev Get list of sponsors for a funding request
     * @param _requestId ID of the funding request
     * @return Array of sponsor addresses
     */
    function getSponsors(uint256 _requestId) external view validRequest(_requestId) returns (address[] memory) {
        return fundingRequests[_requestId].sponsors;
    }

    /**
     * @dev Get contribution amount for a specific sponsor
     * @param _requestId ID of the funding request
     * @param _sponsor Address of the sponsor
     * @return Contribution amount
     */
    function getContribution(uint256 _requestId, address _sponsor) external view validRequest(_requestId) returns (uint256) {
        return fundingRequests[_requestId].contributions[_sponsor];
    }

    /**
     * @dev Get funding request details
     * @param _requestId ID of the funding request
     */
    function getFundingRequestDetails(uint256 _requestId) external view validRequest(_requestId) returns (
        address orgAddress,
        string memory title,
        string memory description,
        uint256 fundingGoal,
        uint256 minimumThreshold,
        uint256 currentAmount,
        uint256 deadline,
        uint256 eventStart,
        uint256 eventEnd,
        FundingStatus status,
        bool eventCompleted
    ) {
        FundingRequest storage request = fundingRequests[_requestId];
        return (
            request.orgAddress,
            request.title,
            request.description,
            request.fundingGoal,
            request.minimumThreshold,
            request.currentAmount,
            request.deadline,
            request.eventStart,
            request.eventEnd,
            request.status,
            request.eventCompleted
        );
    }

    /**
     * @dev Check if an event has passed its end date but not been marked as completed
     * @param _requestId ID of the funding request
     * @return bool True if the event has passed but not been marked as completed
     */
    function isEventOverdue(uint256 _requestId) external view validRequest(_requestId) returns (bool) {
        FundingRequest storage request = fundingRequests[_requestId];
        return block.timestamp > request.eventEnd && !request.eventCompleted &&
               (request.status == FundingStatus.Funded || request.status == FundingStatus.PartiallyFunded);
    }
}

contracts/EduCatalystMilestone.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./Educatalyst.sol";
import "./EducatalystFunding.sol";

/**
 * @title EduCatalystMilestone
 * @dev Manages milestones for funding requests
 */
contract EduCatalystMilestone {
    EduCatalystCore public core;
    EduCatalystFunding public fundingContract;

    struct Milestone {
        uint256 requestId;
        string description;
        uint256 percentage; // Percentage of total funding (in basis points)
        bool released;
        bool approved;
    }

    // requestId => milestone array
    mapping(uint256 => Milestone[]) public milestones;

    event MilestoneCreated(uint256 indexed requestId, uint256 milestoneIndex, string description, uint256 percentage);
    event MilestoneApproved(uint256 indexed requestId, uint256 milestoneIndex);
    event FundsReleased(uint256 indexed requestId, address indexed orgAddress, uint256 amount);
    event RemainingFundsProcessed(uint256 indexed requestId, bool returnedToSponsors);
    event RefundIssued(uint256 indexed requestId, address indexed sponsor, uint256 amount);

    modifier onlyOwner() {
        require(msg.sender == core.owner(), "Only owner can call this function");
        _;
    }

    modifier validRequest(uint256 requestId) {
        (,,,,,,,,, EduCatalystFunding.FundingStatus status,) = fundingContract.getFundingRequestDetails(requestId);
        require(status == EduCatalystFunding.FundingStatus.Funded || 
                status == EduCatalystFunding.FundingStatus.PartiallyFunded, 
                "Request is not funded");
        _;
    }

    constructor(address _coreAddress, address _fundingAddress) {
        core = EduCatalystCore(_coreAddress);
        fundingContract = EduCatalystFunding(_fundingAddress);
    }

    /**
     * @dev Create milestones for a funding request
     * @param _requestId ID of the funding request
     * @param _descriptions Array of milestone descriptions
     * @param _percentages Array of milestone percentages (in basis points)
     */
    function createMilestones(
        uint256 _requestId,
        string[] memory _descriptions,
        uint256[] memory _percentages
    ) external {
        (address orgAddress,,,,,,,,, EduCatalystFunding.FundingStatus status,) = 
            fundingContract.getFundingRequestDetails(_requestId);

        require(msg.sender == orgAddress, "Only the organization that created this request can call this function");
        require(status == EduCatalystFunding.FundingStatus.Funded || 
                status == EduCatalystFunding.FundingStatus.PartiallyFunded, 
                "Request is not funded");
        require(_descriptions.length == _percentages.length, "Arrays must match in length");
        require(milestones[_requestId].length == 0, "Milestones already exist for this request");

        // Verify milestone percentages add up to 100%
        uint256 totalPercentage = 0;
        for (uint i = 0; i < _percentages.length; i++) {
            totalPercentage += _percentages[i];
        }
        require(totalPercentage == 10000, "Milestone percentages must total 100%");

        // Create milestones
        for (uint i = 0; i < _descriptions.length; i++) {
            milestones[_requestId].push(Milestone({
                requestId: _requestId,
                description: _descriptions[i],
                percentage: _percentages[i],
                released: false,
                approved: false
            }));

            emit MilestoneCreated(_requestId, i, _descriptions[i], _percentages[i]);
        }
    }

    /**
     * @dev Approve a milestone for release (only owner for now, could be expanded to a DAO vote)
     * @param _requestId ID of the funding request
     * @param _milestoneIndex Index of the milestone
     */
    function approveMilestone(uint256 _requestId, uint256 _milestoneIndex) external onlyOwner validRequest(_requestId) {
        require(_milestoneIndex < milestones[_requestId].length, "Invalid milestone index");

        Milestone storage milestone = milestones[_requestId][_milestoneIndex];
        require(!milestone.approved, "Milestone already approved");

        milestone.approved = true;
        emit MilestoneApproved(_requestId, _milestoneIndex);
    }

    /**
     * @dev Release funds for a milestone (can only be called by the organization)
     * @param _requestId ID of the funding request
     * @param _milestoneIndex Index of the milestone
     */
    function releaseMilestoneFunds(uint256 _requestId, uint256 _milestoneIndex) external validRequest(_requestId) {
        (address orgAddress,,,,,uint256 currentAmount,,,,,) = fundingContract.getFundingRequestDetails(_requestId);

        require(msg.sender == orgAddress, "Only the organization that created this request can call this function");
        require(_milestoneIndex < milestones[_requestId].length, "Invalid milestone index");

        Milestone storage milestone = milestones[_requestId][_milestoneIndex];
        require(milestone.approved, "Milestone not approved yet");
        require(!milestone.released, "Funds already released for this milestone");

        // Calculate amount to release
        uint256 amountToRelease = (currentAmount * milestone.percentage) / 10000;
        uint256 platformFee = (amountToRelease * core.platformFeePercent()) / 10000;
        uint256 orgAmount = amountToRelease - platformFee;

        milestone.released = true;

        // Transfer funds
        (bool sentOrg, ) = payable(orgAddress).call{value: orgAmount}("");
        require(sentOrg, "Failed to send funds to organization");

        (bool sentOwner, ) = payable(core.owner()).call{value: platformFee}("");
        require(sentOwner, "Failed to send platform fee");

        emit FundsReleased(_requestId, orgAddress, orgAmount);
    }

    /**
     * @dev Get milestones for a funding request
     * @param _requestId ID of the funding request
     * @return Array of Milestone structs
     */
    function getMilestones(uint256 _requestId) external view returns (Milestone[] memory) {
        return milestones[_requestId];
    }

    /**
     * @dev Process remaining funds after specified deadline (can be called by admin)
     * @param _requestId ID of the funding request
     */
    function processRemainingFunds(uint256 _requestId) public validRequest(_requestId) {
        (address orgAddress,,,,,uint256 currentAmount,,,,, bool eventCompleted) = 
            fundingContract.getFundingRequestDetails(_requestId);

        require(eventCompleted, "Event not marked as completed");
        require(msg.sender == core.owner() || msg.sender == orgAddress, 
                "Only owner or organization can process remaining funds");

        // Calculate total released funds
        uint256 totalReleased = 0;
        uint256 totalFunds = currentAmount;

        for (uint i = 0; i < milestones[_requestId].length; i++) {
            if (milestones[_requestId][i].released) {
                totalReleased += (totalFunds * milestones[_requestId][i].percentage) / 10000;
            }
        }

        // If there are unreleased funds
        uint256 remainingFunds = totalFunds - totalReleased;
        if (remainingFunds > 0) {
            bool returnToSponsors = msg.sender == core.owner();

            if (returnToSponsors) {
                // For simplicity, we'll send remaining funds to the core contract
                // which can then handle distribution
                (bool sent, ) = payable(address(core)).call{value: remainingFunds}("");
                require(sent, "Failed to send remaining funds");
            }

            emit RemainingFundsProcessed(_requestId, returnToSponsors);
        }
    }
}

contracts/EduCatalystOrganization.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./Educatalyst.sol";

/**
 * @title EduCatalystOrganization
 * @dev Manages organization registrations and verifications
 */
contract EduCatalystOrganization {
    EduCatalystCore public core;

    enum OrganizationStatus { Unverified, BasicVerification, FullVerification }

    struct Organization {
        address payable orgAddress;
        string name;
        string description;
        string website;
        string socialMedia;
        OrganizationStatus verificationStatus;
        bool exists;
    }

    mapping(address => Organization) public organizations;
    address[] public registeredOrgs;

    event OrganizationRegistered(address indexed orgAddress, string name);
    event OrganizationVerified(address indexed orgAddress, OrganizationStatus status);

    modifier onlyOwner() {
        require(msg.sender == core.owner(), "Only owner can call this function");
        _;
    }

    constructor(address _coreAddress) {
        core = EduCatalystCore(_coreAddress);
    }

    /**
     * @dev Register a new organization on the platform
     * @param _name Name of the organization
     * @param _description Brief description of the organization
     * @param _website Website URL
     * @param _socialMedia Social media handles (comma separated)
     */
    function registerOrganization(
        string memory _name,
        string memory _description,
        string memory _website,
        string memory _socialMedia
    ) external {
        require(!organizations[msg.sender].exists, "Organization already registered");

        organizations[msg.sender] = Organization({
            orgAddress: payable(msg.sender),
            name: _name,
            description: _description,
            website: _website,
            socialMedia: _socialMedia,
            verificationStatus: OrganizationStatus.Unverified,
            exists: true
        });

        registeredOrgs.push(msg.sender);
        emit OrganizationRegistered(msg.sender, _name);
    }

    /**
     * @dev Update organization verification status (only owner)
     * @param _orgAddress Address of the organization
     * @param _status New verification status
     */
    function updateOrganizationStatus(address _orgAddress, OrganizationStatus _status) external onlyOwner {
        require(organizations[_orgAddress].exists, "Organization does not exist");
        organizations[_orgAddress].verificationStatus = _status;
        emit OrganizationVerified(_orgAddress, _status);
    }

    /**
     * @dev Check if an address is a registered organization
     * @param _address The address to check
     * @return Whether the address is a registered organization
     */
    function isOrganization(address _address) external view returns (bool) {
        return organizations[_address].exists;
    }

    /**
 * @dev Get organization details
 * @param _address The organization address
 * @return name The name of the organization
 * @return description The description of the organization
 * @return website The website URL of the organization
 * @return socialMedia The social media handles of the organization
 * @return status The verification status of the organization
 * @return exists Whether the organization exists
 */
    function getOrganizationDetails(address _address) external view returns (
        string memory name,
        string memory description,
        string memory website,
        string memory socialMedia,
        OrganizationStatus status,
        bool exists
    ) {
        Organization storage org = organizations[_address];
        return (
            org.name,
            org.description,
            org.website,
            org.socialMedia,
            org.verificationStatus,
            org.exists
        );
    }

    /**
     * @dev Get list of all registered organizations
     * @return Array of organization addresses
     */
    function getAllOrganizations() external view returns (address[] memory) {
        return registeredOrgs;
    }
}

